ui:View.js:Script:

(function () {
	'use strict';

	var ENTER_KEY = 13;

	return {
		defaults: {
			editing: false
		},

		$classAttributes: ['tobuy', 'inputElement'],

		events: ['remove'],

		editTodo: function (e) {
			e.preventDefault();

			this.set('editing', true);

			this.$.inputElement.$el.focus();
			return false;
		},

		checkTodo: function () {
			var tobuy = this.get('tobuy');

			tobuy.setCompleted(!tobuy.isCompleted());
			tobuy.save();
		},

		preventEditing: function (e) {
			e.stopPropagation();
		},

		updateTodo: function (e) {
			var tobuy;

			if (e.domEvent.keyCode === ENTER_KEY || e.domEvent.type === 'blur') {
				tobuy = this.get('tobuy');

				if (tobuy.hasTitle()) {
					this.set('editing', false);
					tobuy.save();
				} else {
					this.trigger('remove', tobuy);
				}
			}
		},

		triggerOnRemove: function () {
			this.trigger('remove', this.get('tobuy'));
		},

		_renderEditing: function (editing) {
			if (editing) {
				this.addClass('editing');
			} else {
				this.removeClass('editing');
				this.$.inputElement.$el.blur();
			}
		},

		trim: function (title) {
			if (title) {
				return title.trim();
			}
			return '';
		}
	};
})
